[m,n] = size(img);
img = im2uint8(img);
out = zeros(m,n,'uint8');
% M = 0; N = 254;
M = min(img(:));
N = max(img(:));
for i=M:N
    bw = img > i;
    last = bw;
    bw = applylutc(applylutc(bw,lut1),lut2);
    while ~isequal(bw,last)
        last = bw;
        bw = applylutc(bw,lut1);
        bw = applylutc(bw,lut2);
    end
    out = out + uint8(bw);
end
out = im2double(out);

